mapper.select:
SELECT
	`id`, 
	`create_time`, 
	`last_update`, 
	`org_id`, 
	`org_name`, 
	`result_date`, 
	`business_name`, 
	`business_id`, 
	`total_pay_count`, 
	`total_pay_sum`
FROM
	report_business
where 1=1
 and `create_time` = #{createTime} 
 and `last_update` = #{lastUpdate} 
 and `org_id` = #{orgId}
 and `org_name` = #{orgName} 
 and `result_date` = #{resultDate}
 and `business_name` = #{businessName}
 and `business_id` = #{businessId} 
 and `total_pay_count` = #{totalPayCount} 
 and `total_pay_sum` = #{totalPaySum}
ORDER BY
	id DESC

mapper.update:
UPDATE report_business

	`total_pay_count` = total_pay_count +1 ,`total_pay_sum` = total_pay_sum + #{price}

WHERE
`business_id` = #{businessId} and `result_date` = #{resultDate} and `total_pay_sum` = #{totalPaySum}
